import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super();
    console.log('props =', props);
    this.handleChange = this.handleChange.bind(this);
    this.handleClickResult = this.handleClickResult.bind(this);
    this.state = {
      message: '',
      data: ['first item.'],
      inputValue: '',
    };
  }

  handleChange(e) {
    // window.alert('handleClick method');
    // window.alert(e.target.value);
    this.setState({
      message: e.target.value,
    });
  }

  handleItemValueChange(e) {
    this.setState({
      inputValue: e.target.value,
    });
  }

  handleAddItem() {
    const {
      data,
      inputValue,
    } = this.state;
    var newData = data.concat(inputValue);
    this.setState({
      data: newData,
    });
  }

  handleRemoveItem(i) {
    const {
      data,
    } = this.state;
    var arr = data;
    arr.splice(i, 1);
    this.setState({
      data: arr,
    });
  }

  handleClickResult() {
    const {
      message,
    } = this.state;
    window.alert('您輸入的是 = ' + message);
  }

  render() {
    const {
      data,
    } = this.state;
    return (
      <div>
        Hello world<br />
        我的第一隻React Framework前端程式。<br />
        <br />
        <hr />
        <input
          type="text"
          onChange={(e) => this.handleChange(e)}
        />
        &nbsp;&nbsp;
        <input
          type="button"
          onClick={() => this.handleClickResult()}
          value="送出"
        />
        <br />
        <br />
        <div>
          <ol>
            data.map{function (item, i) {
              return (
                <div>

                </div>
              );
            }};
          </ol>
        </div>
      </div>
    );
  }
}

export default App;
